    //   dispatch(addMovies(response.data)) // passing the response.data data is the key which contains all the results
    // } 

    // fetchMovies();
  }, [dispatch]);
  return (
    <div>
      <div className="banner-img"></div>
      <MovieList />
    </div>
  )
}

export default Home
