import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router'
import { useEffect } from 'react';
import { 
       fetchAsyncMSDetails,
       getAllselectedMSDetails
       } from '../../features/movies/movieSlice';
import './MovieDetail.scss';

const MovieDetail= () => {
  //getting the id form the url using use params
const {imdbID} =  useParams();
const dispatch = useDispatch();
const data = useSelector(getAllselectedMSDetails)
console.log(data)
useEffect(() => {
  dispatch(fetchAsyncMSDetails(imdbID))
  return (first) => { second } 
  dispatch()
}, [dispatch,imdbID]); // run useEffect whenever the id changes
//fetch the details of the new id from server and get data to be displayed

return (
  <div className="movie-section">
    {Object.keys(data).length === 0 ? (
      <div>...Loading</div>
    ) : (
      <>
        <div className="section-left">
          <div className="movie-title">{data.Title}</div>
          <div className="movie-rating">
            <span>
              IMDB Rating <i className="fa fa-star"></i> : {data.imdbRating}
            </span>
            <span>
              IMDB Votes <i className="fa fa-thumbs-up"></i> :{" "}
              {data.imdbVotes}
            </span>
            <span>
              Runtime <i className="fa fa-film"></i> : {data.Runtime}
            </span>
            <span>
              Year <i className="fa fa-calendar"></i> : {data.Year}
            </span>
          </div>
          <div className="movie-plot">{data.Plot}</div>
          <div className="movie-info">
            <div>
              <span>Director</span>
              <span>{data.Director}</span>
            </div>
            <div>
              <span>Stars</span>
              <span>{data.Actors}</span>
            </div>
            <div>
              <span>Generes</span>
              <span>{data.Genre}</span>
            </div>
            <div>
              <span>Languages</span>
              <span>{data.Language}</span>
            </div>
            <div>
              <span>Awards</span>
              <span>{data.Awards}</span>
            </div>
          </div>
        </div>
        <div className="section-right">
          <img src={data.Poster} alt={data.Title} />
        </div>
      </>
    )}
  </div>
);
}

export default MovieDetail
